mapper.select:
SELECT b.id, b.bucket_name as bucketName, b.start_date as startDate, b.end_date as endDate, b.bucket_state as bucketState, c.category_name as categoryName FROM bucket b JOIN category c ON b.category_id = c.id WHERE b.user_id = #{userId}
AND b.bucket_state = #{bucketState}
AND c.id = #{category}
ORDER BY id
ORDER BY bucket_name

SELECT b.id, b.bucket_name as bucketName, c.category_name as categoryName, b.end_date as endDate
FROM bucket b
         JOIN bookmark bm ON b.id = bm.bucket_id
         JOIN category c ON b.category_id = c.id
WHERE b.user_id = #{userId}
ORDER BY b.end_date DESC;
