mapper.select:
SELECT count(b.id) as bucketListCount
FROM bucket b JOIN category c
ON b.category_id = c.id
WHERE b.bucket_state = #{bucketState} and b.user_id = #{userId}


        AND c.id = #{categoryId}

SELECT b.id, b.bucket_name, b.start_date, b.end_date, b.bucket_state
FROM bucket b JOIN category c
ON b.category_id = c.id
WHERE user_id = #{userId}


        AND b.categoryId = #{categoryId}

