mapper.select:
SELECT *
FROM user
WHERE social_id = #{socialId}
  AND social_type = #{socialType};

mapper.update:
UPDATE user
SET password = #{password}
WHERE id = #{userId};

mapper.select:
SELECT device_token
FROM user
WHERE id = #{userId}

SELECT alarm_check
FROM user
WHERE id = #{userId}

mapper.update:
UPDATE user
SET alarm_check = #{alarmReadStatus}
WHERE id = #{userId}
